CREATE TABLE REFERENCISTA(   ID_REFERENCISTA             NUMBER(4) PRIMARY KEY,   CODIGO_REFERENCISTA  CHAR(8) NOT NULL,   NOMBRE_COMPLETO        VARCHAR2(100) NOT NULL,);CREATE TABLE PRESTAMO(   ID_PRESTAMO                    NUMBER(4) PRIMARY KEY,   FECHA_PRESTAMO           VARCHAR2(10) NOT NULL,   FECHA_DEVOLUCION        VARCHAR2(10) NOT NULL,   CONSTRAINT FK_ID_LECTOR     FOREIGN KEY (ID_LECTOR)      REFERENCES LECTOR(ID_LECTOR),   CONSTRAINT FK_ID_REFERENCISTA    FOREIGN KEY (ID_REFERENCISTA)      REFERENCES REFERENCISTA(ID_REFERENCISTA),   CONSTRAINT FK_ID_EJEMPLAR  FOREIGN KEY (ID_EJEMPLAR)      REFERENCES EJEMPLAR(ID_EJEMPLAR),);CREATE TABLE LECTOR(   ID_LECTOR                          NUMBER(4) PRIMARY KEY,   CODIGO_LECTOR               CHAR(8) NOT NULL,   APELLIDOS                         VARCHAR2(50) NOT NULL,   NOMBRES                           VARCHAR2(50) NOT NULL,   TIPO_LECTOR                    VARCHAR2(30) NOT NULL,   ESTADO                              CHAR(1) NOT NULL,);CREATE TABLE EJEMPLAR(   ID_EJEMPLAR                    NUMBER(4) PRIMARY KEY,   ORIGEN                               VARCHAR2(30) NOT NULL,   MODALIDAD                       VARCHAR2(20) NOT NULL,   ESTADO                              CHAR(1) NOT NULL,   CONSTRAINT FK_ID_PUBLICACION FOREIGN KEY (ID_PUBLICACION)      REFERENCES PUBLICACION(ID_PUBLICACION));CREATE TABLE PUBLICACION(   ID_PUBLICACION               NUMBER(4) PRIMARY KEY,   CODIGO_PUBLICACION    CHAR(8) NOT NULL,   TITULO                                VARCHAR2(30) NOT NULL,   AUTOR                                VARCHAR2(100) NOT NULL,   EDICION                             NUMBER(2) NOT NULL,   TEMA                                  VARCHAR2(100) NOT NULL,   ESTADO                              CHAR(1) NOT NULL,   TIPO                                    VARCHAR2(20) NOT NULL,);CREATE TABLE MATERIAL_ADICIONAL(   ID_MATERIAL                     NUMBER(4) PRIMARY KEY,   CONSTRAINT FK_ID_PUBLICACION FOREIGN KEY (ID_PUBLICACION)      REFERENCES PUBLICACION(ID_PUBLICACION)   TIPO_CONTENIDO            VARCHAR2(20) NOT NULL,   ESTADO                              CHAR(1) NOT NULL,);